    def calculate_grade(self):
        # Get the current value entered by the user and store the value in variable input_value
        input_value = self.txtScore.text()
        # Input validation to ensure value entered is an integer
        if(input_value.isdigit()):
            # Transform input to integer (value is a string so has to be transformed)
            student_score = int(input_value)
            # Ensure score inputted is between 0 and 100
            if(student_score >= 0 and student_score <= 100):
                # Grade calculation block to assign grade
                if(student_score >= 90):
                    grade = "A"
                elif(student_score >= 80):
                    grade = "B"
                elif (student_score >= 70):
                    grade = "C"
                elif (student_score >= 60):
                    grade = "D"
                else:
                    grade = "F"
                self.txtGrade.setText(grade)
            else:
                # Error message when value is not between 0 and 100
                msg = QMessageBox()
                msg.setText("Please enter a score between 0 and 100.")
                msg.exec()
        else:
            # Error message when value inputted is not an integer
            msg = QMessageBox()
            msg.setText("Please enter a numeric score.")
            msg.exec()

